var gr1 = new GlideRecord('x_270472_abschluss_participants_subject');
gr1.addQuery('u_subject', current.u_subject);
gr1.query();

while(gr1.next()){
	var gr = new GlideRecord('x_270472_abschluss_attestation');
	gr.initialize();
	gr.setValue('u_subject', current.u_subject);
	gr.setValue('u_student', gr1.u_student);
	gr.insert();
}

